import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Level.css';
import Play from './Play';
import End from './End';

const levels = [
    { attempts: 10, image: '/img/4.jpg' },
    { attempts: 7, image: '/img/3.jpg' },
    { attempts: 5, image: '/img/5.jpg' }
];

const Level = ({ level }) => {
    const navigate = useNavigate();
    const minNum = 1;
    const maxNum = 100;
    const [answer, setAnswer] = useState(Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
    const [remainingAttempts, setRemainingAttempts] = useState(levels[level - 1].attempts);
    const [feedback, setFeedback] = useState('');
    const [guess, setGuess] = useState('');
    const [gameOver, setGameOver] = useState(false);
    const [guessedCorrectly, setGuessedCorrectly] = useState(false);
    const [isGameEnded, setIsGameEnded] = useState(false);
    const [remainingTime, setRemainingTime] = useState(60);

    const totalAttempts = levels[level - 1].attempts;

    const handleGuess = () => {
        const guessNumber = Number(guess);
        if (!guessNumber || guessNumber < minNum || guessNumber > maxNum) {
            setFeedback('Please enter a valid number between 1 and 100.');
            return;
        }

        if (guessNumber === answer) {
            setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
            setGuessedCorrectly(true);
            setGameOver(true);
            setIsGameEnded(true);
        } else {
            setRemainingAttempts((prev) => prev - 1);
            if (remainingAttempts - 1 === 0) {
                setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
                setGameOver(true);
                setIsGameEnded(true);
            } else {
                setFeedback(guessNumber > answer ? 'ðŸ“‰ Too high' : 'ðŸ“ˆ Too low');
            }
        }
        setGuess('');
    };

    const calculateScore = () => {
        return 100 - (totalAttempts - remainingAttempts) * 10;
    };

    const handleRestart = () => {
        setAnswer(Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
        setRemainingAttempts(totalAttempts);
        setFeedback('');
        setGuess('');
        setGameOver(false);
        setGuessedCorrectly(false);
        setIsGameEnded(false);
        setRemainingTime(60);
    };

    const handleExitClick = () => {
        const userConfirmed = window.confirm("Are you sure with this decision?");
        if (userConfirmed) {
            navigate('/home');
        }
    };

    useEffect(() => {
        const timer = setInterval(() => {
            setRemainingTime((prevTime) => {
                if (prevTime <= 1) {
                    clearInterval(timer);
                    setGameOver(true);
                    setIsGameEnded(true);
                    setFeedback('â±ï¸ Time is up! Game Over!');
                    return 0;
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timer);
    }, []); 
    useEffect(() => {
        if (gameOver) {
            setRemainingTime(0); // Stops the timer
        }
    }, [gameOver]);

    return (
        <div className="level-container">
            {/* Total Attempts */}
            <div className="info-box total-attempts">
                <p>Total Attempts: {levels[level - 1].attempts}</p>
            </div>

            {/* Remaining Attempts */}
            <div className="info-box remaining-attempts">
                <p>Remaining Attempts: {remainingAttempts}</p>
            </div>

            {/* Main Gameplay Area */}
            <div className="level-panel active">
                <h2 className={guessedCorrectly ? "level-heading-success" : "level-heading"}> Level {level} </h2>
                <div className="guess-container">
                    <input
                        type="number"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                        placeholder="Enter your guess"
                        disabled={gameOver}
                    />
                    
                </div>
                <p className="feedback">{feedback}</p>
                <img src={levels[level - 1].image} alt={`Level ${level}`} />
                <button onClick={handleGuess} disabled={gameOver}>Guess</button>
            </div>

            

            {/* End of Game */}
            {isGameEnded && (
                <End
                    score={calculateScore()}
                    attempts={totalAttempts - remainingAttempts}
                    guessedCorrectly={guessedCorrectly}
                    answer={answer}
                    onClose={handleRestart}
                />
            )}
        </div>
    );
};

export default Level;
