import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import '../CSS/Level.css';

const Level = ({ level, attempts, image, onNextLevel }) => {
  const [guess, setGuess] = useState('');
  const [feedback, setFeedback] = useState('');
  const [remainingAttempts, setRemainingAttempts] = useState(attempts);
  const [answer] = useState(Math.floor(Math.random() * 100) + 1);
  const navigate = useNavigate(); 
  
  const handleGuess = () => {
    const numGuess = Number(guess);
    if (numGuess === answer) {
      setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
    } else {
      setRemainingAttempts(remainingAttempts - 1);
      if (remainingAttempts - 1 === 0) {
        setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
      } else {
        setFeedback(numGuess > answer ? 'ðŸ“‰ Too high!' : 'ðŸ“ˆ Too low!');
      }
    }
    setGuess('');
  };

  const handleRestart = () => {
    setRemainingAttempts(attempts);
    setFeedback('');
    setGuess('');
  };

  return (
    <div className="level-panel level-active">
  <h2 className="level-h2">Level {level}</h2>
  <div className="level-input-group">
    <input
      type="number"
      value={guess}
      onChange={(e) => setGuess(e.target.value)}
      placeholder="Enter your guess"
    />
    <button className="level-guess-button" onClick={handleGuess}>Guess</button>
  </div>
  <p className="level-feedback-success">{feedback}</p>
  <p className="level-attempts">Attempts Left: <span>{remainingAttempts}</span></p>
  <img src={image} alt="Level Image" />
  <div className="level-button-group">
    <button className="level-restart-button" onClick={handleRestart}>Restart Game</button>
    <button className="level-exit-button" onClick={() => navigate('/choose')}>Exit</button>
  </div>
</div>

  );
};

export default Level;
