import React, { useState, useEffect } from 'react';

const Play = () => {
  const [remainingTime, setRemainingTime] = useState(60); // Thời gian ban đầu (60 giây)
  const [attemptsLeft, setAttemptsLeft] = useState(10); // Số lượt chơi ban đầu
  const [guessedNumbers, setGuessedNumbers] = useState([]); // Danh sách các số đã đoán

  useEffect(() => {
    const timer = setInterval(() => {
      setRemainingTime((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer); // Dọn dẹp timer khi hết thời gian
          return 0; // Đảm bảo không giảm thời gian âm
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer); // Dọn dẹp khi component unmount
  }, []);

  const handleGuess = (number) => {
    if (attemptsLeft > 0 && remainingTime > 0) {
      setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
      setAttemptsLeft((prevAttempts) => prevAttempts - 1);
      // Thêm logic kiểm tra số đã đoán ở đây
    }
  };

  return (
    <div>
      <div className="info-panel">
        <h3>Thông tin trò chơi</h3>
        <p>Thời gian còn lại: {remainingTime} giây</p>
        <p>Số lượt chơi còn lại: {attemptsLeft}</p>
        <p>Số đã đoán: {guessedNumbers.join(', ')}</p>
      </div>
      {/* Thêm giao diện để người dùng nhập số và gọi handleGuess */}
    </div>
  );
};

export default Play;
