import React, { useState, useEffect } from 'react';

const Play = ({ remainingTime, attemptsLeft }) => {
  const [timeLeft, setTimeLeft] = useState(remainingTime);
  const [attempts, setAttemptsLeft] = useState(attemptsLeft);
  const [guessedNumbers, setGuessedNumbers] = useState([]);
  const [currentGuess, setCurrentGuess] = useState('');

  useEffect(() => {
    const timerId = setTimeout(() => {
      if (timeLeft > 0) {
        setTimeLeft(prevTime => prevTime - 1);
      } else {
        // Handle game over logic here, e.g., display a message
        clearInterval(timerId);
      }
    }, 1000);

    return () => clearTimeout(timerId);
  }, [timeLeft]);

  const handleGuess = () => {
    const number = parseInt(currentGuess);

    if (attempts > 0 && timeLeft > 0 && !isNaN(number)) {
      setGuessedNumbers([...guessedNumbers, number]);
      setAttemptsLeft(attempts - 1);
      setCurrentGuess('');
    }
  };

  return (
    <div>
      <div className="info-panel">
        <h3>Thông tin trò chơi</h3>
        <p>Thời gian còn lại: {timeLeft} giây</p>
        <p>Số đã đoán: {guessedNumbers.join(', ')}</p>
      </div>
    </div>
  );
};

export default Play;
