import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Level.css';

const Level = ({ level, attempts, image }) => {
  const [guess, setGuess] = useState('');
  const [feedback, setFeedback] = useState('');
  const [remainingAttempts, setRemainingAttempts] = useState(attempts);
  const [answer] = useState(Math.floor(Math.random() * 100) + 1);
  const navigate = useNavigate();

  const handleGuess = () => {
    const numGuess = Number(guess);
    if (numGuess === answer) {
      setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
    } else {
      setRemainingAttempts(remainingAttempts - 1);
      if (remainingAttempts - 1 === 0) {
        setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
      } else {
        setFeedback(numGuess > answer ? 'ðŸ“‰ Too high!' : 'ðŸ“ˆ Too low!');
      }
    }
    setGuess('');
  };

  const handleRestart = () => {
    setRemainingAttempts(attempts);
    setFeedback('');
    setGuess('');
  };

  return (
    <div className="level-panel active">
      <h2 className="h2">Level {level}</h2>
      <div className="input-group">
        <input
          type="number"
          value={guess}
          onChange={(e) => setGuess(e.target.value)}
          placeholder="Enter your guess"
        />
        <button className="guess-button" onClick={handleGuess}>Guess</button>
      </div>
      <p className="feedback-success">{feedback}</p>
      <p className="attempts">Attempts Left: <span>{remainingAttempts}</span></p>
      <img src={image} alt={`Level ${level} Image`} className="level-image" />
      <div className="button-group">
        <button className="restart-button" onClick={handleRestart}>Restart Game</button>
        <button className="exit-button" onClick={() => navigate('/')}>Exit</button>
      </div>
    </div>
  );
};

export default Level;
