import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Level.css';

const LevelGame = ({ maxAttempts }) => {
  const [attempts, setAttempts] = useState(maxAttempts);
  const [feedback, setFeedback] = useState('');
  const [answer] = useState(Math.floor(Math.random() * 100) + 1);
  const navigate = useNavigate();

  const handleGuess = (guess) => {
    if (guess === answer) {
      setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
      setAttempts(maxAttempts);
    } else if (attempts <= 1) {
      setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
    } else {
      setFeedback(guess > answer ? 'ðŸ“‰ Too high!' : 'ðŸ“ˆ Too low!');
      setAttempts(attempts - 1);
    }
  };

  const handleRestart = () => {
    setAttempts(maxAttempts);
    setFeedback('');
  };

  return (
    <div className="level-panel active">
      <h2>Guess the Number</h2>
      <input type="number" placeholder="Enter your guess" />
      <button onClick={() => handleGuess(Number(document.querySelector('input').value))}>
        Guess
      </button>
      <p>{feedback}</p>
      <p>Attempts Left: {attempts}</p>
      <button onClick={handleRestart}>Restart</button>
      <button onClick={() => navigate('/choose')}>Exit</button>
    </div>
  );
};

export default LevelGame;
