import React, { useState, useEffect } from 'react';

const Play = ({ remainingTime, attemptsLeft }) => {
    const [time, setRemainingTime] = useState(remainingTime);
    const [attempts, setAttemptsLeft] = useState(attemptsLeft);
    const [guessedNumbers, setGuessedNumbers] = useState([]);
    const [currentGuess, setCurrentGuess] = useState('');

    useEffect(() => {
        // Reset time and attempts when props change
        setRemainingTime(remainingTime);
        setAttemptsLeft(attemptsLeft);
        setGuessedNumbers([]);
        setCurrentGuess('');
    }, [remainingTime, attemptsLeft]);

    useEffect(() => {
        const timer = setInterval(() => {
            setRemainingTime((prevTime) => {
                if (prevTime <= 1) {
                    clearInterval(timer);
                    return 0;
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    const handleGuess = () => {
        const number = parseInt(currentGuess);

        if (attempts > 0 && time > 0 && !isNaN(number)) {
            setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
            setAttemptsLeft((prevAttempts) => prevAttempts - 1);
            setCurrentGuess('');
        }
    };

    return (
        <div>
            <div className="info-panel">
                <h3>Game Information</h3>
                <p>Time Left: {time} seconds</p>
                <p>Guessed Numbers: {guessedNumbers.join(', ')}</p>
            </div>
        </div>
    );
};

export default Play;
