import React, { useEffect } from 'react';
import '../CSS/End.css';  // Use a separate CSS file for modal-specific styles

const End = ({ score, attempts, guessedCorrectly, answer, elapsedTime, onClose }) => {

  useEffect(() => {
    document.body.classList.add('modal-open');
    return () => {
      document.body.classList.remove('modal-open');
    };
  }, []);

  const handleClose = () => {
    onClose();
  };

  return (
    <div className="modal show" onClick={handleClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>{guessedCorrectly ? "You Won!" : "Game Over"}</h2>
        <table className="end-table">
          <thead>
            <tr>
              <th>Score</th>
              <th>Attempts Used</th>
              <th>Correct Answer</th>
              <th>Elapsed Time (seconds)</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{score}</td>
              <td>{attempts}</td>
              <td>{answer}</td>
              <td>{elapsedTime}</td>
            </tr>
          </tbody>
        </table>
        <div className="button-group">
          <button className="handleClose" onClick={handleClose}>Exit</button>
        </div>
      </div>
    </div>
  );
};

export default End;
