import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Level.css';
import End from './End';

const levels = [
    { attempts: 10, image: '/img/4.jpg' },
    { attempts: 7, image: '/img/3.jpg' },
    { attempts: 5, image: '/img/5.jpg' }
];

const Level = ({ level }) => {
    const navigate = useNavigate();
    const minNum = 1;
    const maxNum = 100;
    const [answer, setAnswer] = useState(Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
    const [remainingAttempts, setRemainingAttempts] = useState(levels[level - 1].attempts);
    const [feedback, setFeedback] = useState('');
    const [guess, setGuess] = useState('');
    const [guessedNumbers, setGuessedNumbers] = useState([]);
    const [gameOver, setGameOver] = useState(false);
    const [guessedCorrectly, setGuessedCorrectly] = useState(false);
    const [isGameEnded, setIsGameEnded] = useState(false);
    const [remainingTime, setRemainingTime] = useState(60);

    const levelConfig = [
        { time: 60 },
        { time: 45 },
        { time: 30 }
    ];

    const handleGuess = () => {
        const guessNumber = Number(guess);
        if (!guessNumber || guessNumber < minNum || guessNumber > maxNum) {
            setFeedback('Please enter a valid number between 1 and 100.');
            return;
        }

        setGuessedNumbers((prev) => [...prev, guessNumber]);

        if (guessNumber === answer) {
            setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
            setGuessedCorrectly(true);
            setGameOver(true);
            setIsGameEnded(true);
        } else {
            setRemainingAttempts((prev) => prev - 1);
            if (remainingAttempts - 1 === 0) {
                setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
                setGameOver(true);
                setIsGameEnded(true);
            } else {
                setFeedback(guessNumber > answer ? 'ðŸ“‰ Too high' : 'ðŸ“ˆ Too low');
            }
        }
        setGuess('');
    };

    useEffect(() => {
        const timer = setInterval(() => {
            setRemainingTime((prevTime) => {
                if (prevTime <= 1) {
                    clearInterval(timer);
                    setGameOver(true);
                    setIsGameEnded(true);
                    setFeedback('â±ï¸ Time is up! Game Over!');
                    return 0;
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timer);
    }, []);

    const handleRestart = () => {
        setAnswer(Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
        setRemainingAttempts(levels[level - 1].attempts);
        setFeedback('');
        setGuess('');
        setGuessedNumbers([]);
        setGameOver(false);
        setGuessedCorrectly(false);
        setIsGameEnded(false);
        setRemainingTime(levelConfig[level - 1].time);
    };

    return (
        <div className="level-container">
            <div className="level-panel">
                <div className="game-section">
                    <h2 className={guessedCorrectly ? "level-heading-success" : "level-heading"}>Level {level}</h2>
                    <input
                        type="number"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                        placeholder="Enter your guess"
                        disabled={gameOver}
                    />
                    <p
                        className={
                            feedback.includes('high')
                                ? 'tooHigh'
                                : feedback.includes('low')
                                ? 'tooLow'
                                : 'feedback-error'
                        }
                    >
                        {feedback}
                    </p>
                    <img src={levels[level - 1].image} alt={`Level ${level}`} />
                    <button onClick={handleGuess} disabled={gameOver}>
                        Guess
                    </button>
                </div>
                <div className="info-section">
                    <div className="info-box">
                        <p>Initial Attempts: {levels[level - 1].attempts}</p>
                    </div>
                    <div className="info-box">
                        <p>Remaining Attempts: {remainingAttempts}</p>
                    </div>
                    <div className="info-box">
                        <p>Time Left: {remainingTime} seconds</p>
                    </div>
                    <div className="info-box">
                        <p>Guessed Numbers:</p>
                        <ul>
                            {guessedNumbers.map((num, index) => (
                                <li key={index}>{num}</li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
            {isGameEnded && (
                <End
                    score={100 - (levels[level - 1].attempts - remainingAttempts) * 10}
                    attempts={levels[level - 1].attempts - remainingAttempts}
                    guessedCorrectly={guessedCorrectly}
                    answer={answer}
                    onClose={handleRestart}
                />
            )}
        </div>
    );
};

export default Level;
