import React, { useState } from 'react';
import './Level.css'; // Đảm bảo bạn đã import CSS

const Level = ({ answer, maxNum, minNum }) => {
    const [guess, setGuess] = useState('');
    const [feedback, setFeedback] = useState('');
    const [remainingAttempts, setRemainingAttempts] = useState(5);
    const [guessedCorrectly, setGuessedCorrectly] = useState(false);
    const [gameOver, setGameOver] = useState(false);

    const handleGuess = () => {
        const guessNumber = Number(guess);
        if (!guessNumber || guessNumber < minNum || guessNumber > maxNum) {
            setFeedback('Please enter a valid number between 1 and 100.');
            return; // Dừng lại nếu nhập không hợp lệ
        }

        if (guessNumber === answer) {
            setFeedback('🎉 Congratulations! You guessed the correct number!');
            setGuessedCorrectly(true); 
            setGameOver(true);
        } else {
            setRemainingAttempts(prev => prev - 1);
            if (remainingAttempts - 1 === 0) {
                setFeedback(`😞 Game Over! The number was ${answer}.`);
                setGameOver(true);
            } else {
                setFeedback(guessNumber > answer ? ' 📉 too-high' : '📈 too-low');
            }
        }
        setGuess(''); // Reset guess input after each attempt
    };

    return (
        <div>
            <input 
                type="number" 
                value={guess} 
                onChange={(e) => setGuess(e.target.value)} 
                disabled={gameOver} // Vô hiệu hóa ô nhập nếu game đã kết thúc
            />
            <button onClick={handleGuess} disabled={gameOver}>Guess</button>
            <p className={feedback.includes("Please enter a valid number") ? "feedback-error" : (feedback.includes("Game Over") ? "feedback-error" : guessedCorrectly ? "feedback-success" : "")}>
                {feedback}
            </p>
            <p>Remaining Attempts: {remainingAttempts}</p>
        </div>
    );
};

export default Level;
