import React, { useState, useEffect } from 'react';

const Play = ({ initialTime }) => {
    const [time, setRemainingTime] = useState(initialTime);
    const [guessedNumbers, setGuessedNumbers] = useState([]);
    const [currentGuess, setCurrentGuess] = useState('');

    // Giảm thời gian mỗi giây
    useEffect(() => {
        const timer = setInterval(() => {
            setRemainingTime((prevTime) => {
                if (prevTime <= 1) {
                    clearInterval(timer);
                    return 0;
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timer); // Dọn dẹp bộ đếm khi component unmount
    }, []);

    // Xử lý khi người chơi đoán
    const handleGuess = () => {
        const number = parseInt(currentGuess, 10);

        if (!isNaN(number) && !guessedNumbers.includes(number)) {
            setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
        }

        setCurrentGuess(''); // Reset input sau mỗi lần đoán
    };

    return (
        <div>
            <div className="info-panel">
                <h3>Thông tin trò chơi</h3>
                <p>Thời gian còn lại: {time} giây</p>
                <p>Số đã đoán: {guessedNumbers.length > 0 ? guessedNumbers.join(', ') : 'Chưa có số nào được đoán'}</p>
            </div>
        </div>
    );
};

export default Play;
