import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './Components/Home';
import Choose from './Components/Choose';
import Level from './Components/Level'; // Import the Level component
import './App.css';

const App = () => {
  const [level, setLevel] = useState(1);

  const levels = [
    { attempts: 7, image: './img/hinh-nen-desktop.jpg' },
    { attempts: 5, image: './img/level2.jpg' },
    { attempts: 3, image: './img/level3.jpg' },
  ];

  const handleNextLevel = () => {
    if (level < levels.length) {
      setLevel(level + 1);
    }
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/choose" element={<Choose />} />
        <Route
          path="/level"
          element={
            <Level
              level={level}
              attempts={levels[level - 1].attempts}
              image={levels[level - 1].image}
              onNextLevel={handleNextLevel}
            />
          }
        />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
