import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import '../CSS/Login.css';

const Login = ({ setIsLoggedIn }) => { // Nhận prop setIsLoggedIn
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); 

  const handleSubmit = async (e) => {
    e.preventDefault(); // Ngăn chặn reload trang

    if (username && password) {
        try {
            const response = await fetch(`http://localhost:8080/api/players/login?username=${username}&password=${password}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (response.ok) {
                const playerData = await response.json(); // Lấy dữ liệu người chơi
                console.log(playerData); // In ra dữ liệu người chơi (idPlayer, username, password)
                setIsLoggedIn(true);
                navigate('/home');
            } else {
                alert('Failed to login. Invalid username or password.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred. Please try again.');
        }
    } else {
        alert('Please enter username and password.');
    }
};

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>User name:</label>
          <input  type="text"  value={username} onChange={(e) => setUsername(e.target.value)} required />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
