import React, { useState } from 'react';
import End from './End'; // Nhập End.js
import '../CSS/Level.css';

const Level = () => {
    const [isGameEnded, setIsGameEnded] = useState(false);
    const [remainingAttempts, setRemainingAttempts] = useState(10);
    const [currentGuess, setCurrentGuess] = useState('');
    const [feedback, setFeedback] = useState('');
    const [answer, setAnswer] = useState(Math.floor(Math.random() * 100) + 1); // Số bí mật
    const [level, setLevel] = useState(1); // Cấp độ hiện tại

    const handleGuess = () => {
        const guessNumber = parseInt(currentGuess);

        if (guessNumber === answer) {
            setFeedback('🎉 Congratulations! You guessed the correct number!');
            setIsGameEnded(true); // Đánh dấu trò chơi đã kết thúc
        } else {
            setRemainingAttempts(prev => prev - 1);
            if (remainingAttempts - 1 === 0) {
                setFeedback(`😞 Game Over! The number was ${answer}.`);
                setIsGameEnded(true); // Đánh dấu trò chơi đã kết thúc
            } else {
                setFeedback(guessNumber > answer ? '📉 too-high' : '📈 too-low');
            }
        }
        setCurrentGuess('');
    };

    const calculateScore = () => {
        return 100 - (10 - remainingAttempts) * 10; // Tính toán điểm số
    };

    return (
        <div className="level-container">
            <h2>Level {level}</h2>
            <p>Attempts left: {remainingAttempts}</p>
            <input 
                type="number" 
                value={currentGuess} 
                onChange={(e) => setCurrentGuess(e.target.value)} 
            />
            <button onClick={handleGuess}>Guess</button>
            <p>{feedback}</p>

            {isGameEnded && (
                <End 
                    score={calculateScore()} // Tính toán điểm số
                    attempts={10 - remainingAttempts} // Số lượt đã sử dụng
                    onClose={() => setIsGameEnded(false)} // Hàm để đóng End.js
                />
            )}
        </div>
    );
};

export default Level;
