import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios';
import '../CSS/Login.css';

const Login = ({ setIsLoggedIn }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); 

  useEffect(() => {
    // Kiểm tra thông tin đăng nhập từ localStorage
    const storedUsername = localStorage.getItem('username');
    const storedPassword = localStorage.getItem('password');
    if (storedUsername && storedPassword) {
      setUsername(storedUsername);
      setPassword(storedPassword);
      handleSubmit(null, storedUsername, storedPassword);
    }
  }, []);

  const handleSubmit = async (e, storedUsername, storedPassword) => {
    if (e) e.preventDefault(); // Ngăn chặn reload trang nếu có sự kiện e

    const user = storedUsername || username;
    const pass = storedPassword || password;

    if (user && pass) {
      try {
        // Gửi thông tin đăng nhập đến backend
        const response = await axios.post('http://localhost:8080/api/players', {
          username: user,
          password: pass,
        });

        // Kiểm tra phản hồi từ server
        if (response.status === 201) {
          console.log('Đăng nhập thành công:', response.data);
          setIsLoggedIn(true);
          localStorage.setItem('username', user);
          localStorage.setItem('password', pass);
          navigate('/home'); 
        } else {
          alert('Đăng nhập không thành công. Vui lòng kiểm tra lại thông tin.');
        }
      } catch (error) {
        console.error('Có lỗi xảy ra:', error);
        alert('Đăng nhập không thành công. Vui lòng kiểm tra lại thông tin.');
      }
    } else {
      alert('Please enter username and password.');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>User name:</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
