import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Home.css';
import Law from './Law';
const Home = ({ isLoggedIn }) => { 
    const [showLaw, setShowLaw] = useState(false);
    const navigate = useNavigate(); 

    const handleOpenLaw = () => {
        setShowLaw(true);
    };

    const handleCloseLaw = () => {
        setShowLaw(false);
    };

    return (
        <div className="Home">
            <div className="pattern"></div>
            <img src="./img/1.png" className="left-image" alt="1" /> 
            <div className="title">ADL</div>
            <div className="subtitle">Number Guessing Game</div>
            <img src="./img/9.png" class="right-image" alt="9" />
            {/* <button className="play-button-1" onClick={handleOpenLaw}>Play</button> */}
            {/* <div className="icons">
                <span className="icon"><i className="bi bi-gear"></i></span>
            </div> */}
            <div className="info-card">
                <div className="card-title">Welcome to the Game!</div>
                <div className="card-content">Guess the number in as few attempts as possible. Good luck!</div>
            </div>
            {showLaw && <Law onClose={handleCloseLaw} />}
        </div>
    );
};

export default Home;
