import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './Components/Home';
import Choose from './Components/Choose';
import Level from './Components/Level';
import './App.css';
import img1 from './img/4.jpg';
import img2 from './img/3.jpg';
import img3 from './img/5.jpg';



const App = () => {
  const [level, setLevel] = useState(1);

  const levels = [
    { attempts: 7, image: img1 },
    { attempts: 5, image: img2 },
    { attempts: 3, image: img3 },
  ];

  const handleNextLevel = () => {
    if (level < levels.length) {
      setLevel(level + 1);
      window.location.href = `/level/${level + 1}`;
    }
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/choose" element={<Choose />} />
        {levels.map((levelData, index) => (
          <Route
            key={index}
            path={`/level/${index + 1}`}
            element={
              <Level
                level={index + 1}
                attempts={levelData.attempts}
                image={levelData.image}
              />
            }
          />
        ))}
      </Routes>
    </BrowserRouter>
  );
};

export default App;
