import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './Components/Home';
import Choose from './Components/Choose';
import Level from './Components/Level';
import './App.css';

const App = () => {
  const [level, setLevel] = useState(1);

 const levels = [
  { attempts: 7, image: <img src="/img/4.jpg" className="left-image" alt="Level 1" /> },
  { attempts: 5, image: <img src="/img/3.jpg" className="left-image" alt="Level 2" /> },
  { attempts: 3, image: <img src="/img/5.jpg" className="left-image" alt="Level 3" /> },
   ];

  const handleNextLevel = () => {
    if (level < levels.length) {
      setLevel(level + 1);
      window.location.href = `/level/${level + 1}`;
    }
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/choose" element={<Choose />} />
        {levels.map((levelData, index) => (
          <Route
            key={index}
            path={`/level/${index + 1}`}
            element={
              <Level
                level={index + 1}
                attempts={levelData.attempts}
                image={levelData.image}
              />
            }
          />
        ))}
      </Routes>
    </BrowserRouter>
  );
};

export default App;
