import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Home.css';
import Law from './Law';

const Home = ({ isLoggedIn }) => { 
    const [showLaw, setShowLaw] = useState(false);
    const [currentLevel, setCurrentLevel] = useState(0);
    const navigate = useNavigate(); 

    const handleOpenLaw = (level) => {
        setCurrentLevel(level);
        setShowLaw(true);
    };

    const handleCloseLaw = () => {
        setShowLaw(false);
    };

    useEffect(() => {
        if (showLaw) {
            document.body.classList.add('modal-open'); // Thêm lớp khi modal mở
        } else {
            document.body.classList.remove('modal-open'); // Xóa lớp khi modal đóng
        }
    }, [showLaw]);

    return (
        <div className="Home">
            <h1>Welcome to the Number Guessing Game!</h1>
            <button onClick={() => handleOpenLaw(1)}>Level 1</button>
            <button onClick={() => handleOpenLaw(2)}>Level 2</button>
            <button onClick={() => handleOpenLaw(3)}>Level 3</button>
            {showLaw && <Law onClose={handleCloseLaw} level={currentLevel} />}
            <div className="info-card">
                <div className="card-title">Welcome to the Game!</div>
                <div className="card-content">Guess the number in as few attempts as possible. Good luck!</div>
            </div>
            {showLaw && <Law onClose={handleCloseLaw} level={currentLevel} />} {/* Truyền level vào Law */}
        </div>
    );
};

export default Home;
