import React, { useState, useEffect } from 'react';

const Play = ({ initialTime, attemptsLeft }) => {
    const [time, setRemainingTime] = useState(initialTime);
    const [attempts, setAttemptsLeft] = useState(attemptsLeft);
    const [guessedNumbers, setGuessedNumbers] = useState([]);
    const [currentGuess, setCurrentGuess] = useState('');

    useEffect(() => {
        // Sync attempts when prop changes
        setAttemptsLeft(attemptsLeft);
    }, [attemptsLeft]);

    useEffect(() => {
        // Timer logic
        const timer = setInterval(() => {
            setRemainingTime((prevTime) => {
                if (prevTime <= 1) {
                    clearInterval(timer);
                    return 0;
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timer); // Cleanup timer on component unmount
    }, []);

    const handleGuess = () => {
        const number = parseInt(currentGuess, 10);

        if (isNaN(number)) {
            alert('Please enter a valid number!');
            return;
        }

        if (guessedNumbers.includes(number)) {
            alert('You already guessed that number!');
            setCurrentGuess('');
            return;
        }

        if (attempts > 0 && time > 0) {
            setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
            setAttemptsLeft((prevAttempts) => prevAttempts - 1);
            setCurrentGuess('');
        } else {
            alert('No attempts left or time is up!');
        }
    };

    return (
        <div className="play-container">
            <div className="info-panel">
                <h3>Game Info</h3>
                <p>Time Remaining: {time} seconds</p>
                <p>Attempts Left: {attempts}</p>
                <p>Guessed Numbers: {guessedNumbers.length > 0 ? guessedNumbers.join(', ') : 'None'}</p>
            </div>
            <div className="guess-input">
                <input
                    type="number"
                    value={currentGuess}
                    onChange={(e) => setCurrentGuess(e.target.value)}
                    placeholder="Enter your guess"
                    disabled={time === 0 || attempts === 0}
                />
                <button onClick={handleGuess} disabled={time === 0 || attempts === 0}>
                    Submit Guess
                </button>
            </div>
            {time === 0 && <p className="time-up">Time's up!</p>}
            {attempts === 0 && <p className="no-attempts">No attempts remaining!</p>}
        </div>
    );
};

export default Play;
