import React, { useEffect } from 'react';
import '../CSS/Law.css';
import { useNavigate } from 'react-router-dom'; 

const Law = ({ onClose, level }) => {
  const navigate = useNavigate(); 

  useEffect(() => {
    document.body.classList.add('modal-open'); // Thêm lớp CSS để ngăn cuộn trang
    return () => {
      document.body.classList.remove('modal-open'); // Xóa lớp CSS khi modal đóng
    };
  }, []);

  const handleClose = () => {
    onClose(); 
  };

  const handleStart = () => {
    onClose(); 
    navigate(`/level/${level}`); // Chuyển hướng đến Level.js với level tương ứng
  };

  // Thông tin cho từng level
  const levelInfo = {
    1: { attempts: 10, time: '60 phút' },
    2: { attempts: 7, time: '45 phút' },
    3: { attempts: 5, time: '30 phút' },
  };

  return (
    <div className="modal show" onClick={handleClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2> {`Level ${level}`}: </h2>
        <p>Trong level này, bạn sẽ có:</p>
        <ul>
            <li>Số lượt chơi: {levelInfo[level].attempts}</li>
            <li>Thời gian: {levelInfo[level].time}</li>
            <li>Cách tính điểm: </li>
        </ul>
        <button className="handleClose" onClick={handleClose}>Close</button>
        <button className="handleStart" onClick={handleStart}>Start</button>
      </div>
    </div>
  );
};

export default Law;
