import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import '../CSS/Level.css';

const LevelGame = ({ maxAttempts }) => {
  const { state } = useLocation();
  const levelImg = state?.levelImg || './img/default.jpg'; // Sử dụng ảnh mặc định nếu không có ảnh được truyền
  const [attempts, setAttempts] = useState(maxAttempts);
  const [feedback, setFeedback] = useState('');
  const [answer] = useState(Math.floor(Math.random() * 100) + 1);
  const navigate = useNavigate();

  const handleGuess = (guess) => {
    if (guess === answer) {
      setFeedback('🎉 Congratulations! You guessed the correct number!');
      setAttempts(maxAttempts);
    } else if (attempts <= 1) {
      setFeedback(`😞 Game Over! The number was ${answer}.`);
    } else {
      setFeedback(guess > answer ? '📉 Too high!' : '📈 Too low!');
      setAttempts(attempts - 1);
    }
  };

  const handleRestart = () => {
    setAttempts(maxAttempts);
    setFeedback('');
  };

  return (
    <div className="level-panel active">
      <h2>Level Game</h2>
      <input type="number" id="guess-input" placeholder="Enter your guess" />
      <button id="guess-button" onClick={() => handleGuess(Number(document.getElementById('guess-input').value))}>
        Guess
      </button>
      <p id="feedback">{feedback}</p>
      <p>Attempts Left: <span id="attempts">{attempts}</span></p>
      <img src={levelImg} alt="Level Image" />
      <button id="restart-button" className={feedback === '' ? 'hidden' : ''} onClick={handleRestart}>
        Restart Game
      </button>
      <button className="exit-btn" onClick={() => navigate('/choose')}>
        Exit
      </button>
    </div>
  );
};

export default LevelGame;
