import React, { useState, useEffect } from 'react';

const Play = ({ remainingTime, attemptsLeft }) => {
  const [time, setRemainingTime] = useState(remainingTime); 
  const [attempts, setAttemptsLeft] = useState(attemptsLeft); 
  const [guessedNumbers, setGuessedNumbers] = useState([]); 
  const [currentGuess, setCurrentGuess] = useState(''); 
  const [timeLeft, setTimeLeft] = useState(remainingTime);

  useEffect(() => {
      setTimeLeft(remainingTime); // Reset time when reset is triggered
  }, [reset, remainingTime]);

  useEffect(() => {
      if (timeLeft > 0) {
          const timer = setTimeout(() => setTimeLeft(prev => prev - 1), 1000);
          return () => clearTimeout(timer);
      }
  }, [timeLeft]);
  useEffect(() => {
    const timer = setInterval(() => {
      setRemainingTime((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          return 0; 
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer); 
  }, []);

  const handleGuess = () => {
    const number = parseInt(currentGuess); // Chuyển đổi giá trị nhập thành số

    if (attempts > 0 && time > 0 && !isNaN(number)) {
      setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
      setAttemptsLeft((prevAttempts) => prevAttempts - 1);
      setCurrentGuess(''); 
    }
  };

  return (
    <div>
      <div className="info-panel">
        <h3>Thông tin trò chơi</h3>
        <p>Thời gian còn lại: {time} giây</p>
        <p>Số đã đoán: {guessedNumbers.join(', ')}</p>
      </div>
    </div>
  );
};

export default Play;
