import React, { useState } from 'react';
import { Link } from 'react-router-dom'; 
import '../CSS/Level.css';
const Level = () => {
    const levels = [
        { level: 1, attempts: 7, image: './img/hinh-nen-desktop.jpg'},
        { level: 2, attempts: 5, image: './img/ngam-va-tai-hinh-nen-dep-cho-may-tinh-556444149b46f6.jpg'},
        { level: 3, attempts: 3, image: './img/2.jpg'}
    ];

    const generateRandomNumber = () => {
        const minNum = 1;
        const maxNum = 100;
        return Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum;
    };

    const [currentLevel, setCurrentLevel] = useState(0);
    const [answer, setAnswer] = useState(generateRandomNumber());
    const [attempts, setAttempts] = useState(levels[currentLevel].attempts);
    const [feedback, setFeedback] = useState('');
    const [guess, setGuess] = useState('');
    const [gameOver, setGameOver] = useState(false);

    const handleGuess = () => {
        const guessNumber = Number(guess);
        if (!guessNumber || guessNumber < 1 || guessNumber > 100) {
            setFeedback('Please enter a valid number between 1 and 100.');
            return;
        }

        if (guessNumber === answer) {
            setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
            if (currentLevel < levels.length - 1) {
                setTimeout(() => {
                    setCurrentLevel(currentLevel + 1);
                    resetGame(levels[currentLevel + 1].attempts);
                }, 2000);
            } else {
                endGame();
            }
        } else {
            setAttempts(attempts - 1);
            if (attempts - 1 === 0) {
                setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
                endGame();
            } else {
                setFeedback(guessNumber > answer ? 'ðŸ“‰ Too high!' : 'ðŸ“ˆ Too low!');
            }
        }

        setGuess('');
    };

    const resetGame = (newAttempts) => {
        setAttempts(newAttempts);
        setAnswer(generateRandomNumber());
        setFeedback('');
        setGuess('');
        setGameOver(false);
    };

    const restartGame = () => {
        setCurrentLevel(0);
        resetGame(levels[0].attempts);
    };

    const endGame = () => {
        setGameOver(true);
    };

    const exitGame = () => {
        window.location.href = 'choose.html';
    };

    return (
        <div className="level-panel active">
            <h2>Level {levels[currentLevel].level}</h2>
            <input
                type="number"
                value={guess}
                onChange={(e) => setGuess(e.target.value)}
                placeholder="Enter your guess"
                disabled={gameOver}
            />
            <button onClick={handleGuess} disabled={gameOver}>Guess</button>
            <p>{feedback}</p>
            <p>Attempts Left: <span>{attempts}</span></p>
            <img src={levels[currentLevel].image} alt={`Level ${levels[currentLevel].level} Image`} />
            <button onClick={restartGame} className={gameOver ? '' : 'hidden'}>Restart Game</button>
            <button onClick={exitGame} className="exit-btn">Exit</button>
        </div>
    );
};

export default Level;
