import React, { useState, useEffect } from 'react';

const Play = () => {
  const [remainingTime, setRemainingTime] = useState(60); 
  const [attemptsLeft, setAttemptsLeft] = useState(10); 
  const [guessedNumbers, setGuessedNumbers] = useState([]); 
  const [currentGuess, setCurrentGuess] = useState(''); 

  useEffect(() => {
    const timer = setInterval(() => {
      setRemainingTime((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          return 0; 
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer); // Cleanup on component unmount
  }, []);

  const handleGuess = () => {
    const number = parseInt(currentGuess); // Convert input to a number

    if (attemptsLeft > 0 && remainingTime > 0 && !isNaN(number)) {
      setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
      setAttemptsLeft((prevAttempts) => prevAttempts - 1);
      setCurrentGuess(''); // Clear input after guess
    } else {
      alert("Please enter a valid number.");
    }
  };

  return (
    <div>
      <div className="info-panel">
        <h3>Game Information</h3>
        <p>Remaining Time: {remainingTime} seconds</p>
        <p>Attempts Left: {attemptsLeft}</p>
        <p>Guessed Numbers: {guessedNumbers.join(', ')}</p>
      </div>
      <input
        type="text"
        value={currentGuess}
        onChange={(e) => setCurrentGuess(e.target.value)}
        placeholder="Enter your guess"
      />
      <button onClick={handleGuess} disabled={attemptsLeft === 0 || remainingTime === 0}>
        Guess
      </button>
    </div>
  );
};

export default Play;
