import React, { useState, useEffect } from 'react';

const Play = ({ remainingTime, attemptsLeft }) => {
  const [time, setTime] = useState(remainingTime); 
  const [attempts, setAttempts] = useState(attemptsLeft); 
  const [guessedNumbers, setGuessedNumbers] = useState([]); 
  const [currentGuess, setCurrentGuess] = useState(''); 

  useEffect(() => {
    setTime(remainingTime); // Update time when props change
    setAttempts(attemptsLeft); // Update attempts when props change
    setGuessedNumbers([]); // Reset guessed numbers on restart
  }, [remainingTime, attemptsLeft]);

  useEffect(() => {
    const timer = setInterval(() => {
      setTime((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          return 0; 
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer); 
  }, []);

  const handleGuess = () => {
    const number = parseInt(currentGuess); // Convert input to number

    if (attempts > 0 && time > 0 && !isNaN(number)) {
      setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
      setAttempts((prevAttempts) => prevAttempts - 1);
      setCurrentGuess(''); 
    }
  };

  return (
    <div>
      <div className="info-panel">
        <h3>Game Information</h3>
        <p>Time Remaining: {time} seconds</p>
        <p>Attempts Left: {attempts}</p>
        <p>Guessed Numbers: {guessedNumbers.join(', ')}</p>
      </div>
      <div className="guess-input">
        <input 
          type="number" 
          value={currentGuess} 
          onChange={(e) => setCurrentGuess(e.target.value)} 
          placeholder="Enter your guess" 
        />
        <button onClick={handleGuess} disabled={attempts <= 0 || time <= 0}>Guess</button>
      </div>
    </div>
  );
};

export default Play;
