import React from 'react';
import '../CSS/Law.css';
import { useNavigate } from 'react-router-dom'; 

const Law = ({ onClose }) => {
  const navigate = useNavigate(); 

  const handleClose = () => {
    onClose(); 
  };

  const handleStart = () => {
    onClose(); 
    navigate('/level'); 
  };

  return (
    <div className="modal show" onClick={handleClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>Những điều cần chú ý: </h2>
        <p>Trong level 1 này, bạn sẽ có:</p>
        <ul>
            <li>Số lượt chơi: 10</li>
            <li>Thời gian: 60 phút</li>
            <li> Cách tính điểm: </li>
        </ul>
        <button className="handleClose" onClick={handleClose}>Close</button>
        <button className="handleStart" onClick={handleStart}>Start</button>
      </div>
    </div>
  );
};

export default Law;
