import React, { useState, useEffect } from 'react';
import './Level.css';

const Level = ({ totalAttempts, remainingTime, attemptsLeft }) => {
  const [time, setRemainingTime] = useState(remainingTime);
  const [attempts, setAttemptsLeft] = useState(attemptsLeft);

  useEffect(() => {
    const timer = setInterval(() => {
      setRemainingTime((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  return (
    <div className="level-layout">
      {/* Total Attempts on the Left */}
      <div className="info-box total-attempts">
        <p>Total Attempts: {totalAttempts}</p>
      </div>

      {/* Game Area in the Center */}
      <div className="level-content">
        <h2>Game Area</h2>
        {/* Your main game logic or UI components */}
      </div>

      {/* Remaining Attempts on the Right */}
      <div className="info-box remaining-attempts">
        <p>Remaining Attempts: {attempts}</p>
      </div>

      {/* Remaining Time at the Bottom */}
      <div className="info-box remaining-time">
        <p>Time Remaining: {time} seconds</p>
      </div>
    </div>
  );
};

export default Level;
