import React, { useState, useEffect } from 'react';

const Play = ({ remainingTime, attemptsLeft, totalAttempts }) => {
  const [time, setRemainingTime] = useState(remainingTime);
  const [attempts, setAttemptsLeft] = useState(attemptsLeft);
  const [currentGuess, setCurrentGuess] = useState('');
  const [guessedNumbers, setGuessedNumbers] = useState([]);

  useEffect(() => {
    setRemainingTime(remainingTime);
  }, [remainingTime]);

  useEffect(() => {
    const timer = setInterval(() => {
      setRemainingTime((prevTime) => {
        if (prevTime <= 1) {
          clearInterval(timer);
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleGuess = () => {
    const number = parseInt(currentGuess);

    if (attempts > 0 && time > 0 && !isNaN(number)) {
      setGuessedNumbers((prevGuesses) => [...prevGuesses, number]);
      setAttemptsLeft((prevAttempts) => prevAttempts - 1);
      setCurrentGuess('');
    }
  };

  return (
    <div className="play-container">
      {/* Total Attempts */}
      <div className="info-box total-attempts">
        <p>Total Attempts: {totalAttempts}</p>
      </div>

      {/* Remaining Attempts */}
      <div className="info-box remaining-attempts">
        <p>Remaining Attempts: {attempts}</p>
      </div>

      {/* Remaining Time */}
      <div className="info-box remaining-time">
        <p>Time Remaining: {time} seconds</p>
      </div>

      {/* Guess Input */}
      <div className="guess-input">
        <input
          type="number"
          value={currentGuess}
          onChange={(e) => setCurrentGuess(e.target.value)}
          placeholder="Enter your guess"
        />
        <button onClick={handleGuess} disabled={attempts <= 0 || time <= 0}>
          Guess
        </button>
      </div>
    </div>
  );
};

export default Play;
