import React, { useState } from 'react';
// import { Link } from 'react-router-dom'; // XÃ³a dÃ²ng nÃ y náº¿u khÃ´ng sá»­ dá»¥ng Link
import '../CSS/Level.css';

const Level1 = () => {
  const minNum = 1;
  const maxNum = 100;
  const [answer, setAnswer] = useState(Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
  const [attempts, setAttempts] = useState(7);
  const [guess, setGuess] = useState('');
  const [feedback, setFeedback] = useState('');

  const handleGuess = () => {
    const guessNum = Number(guess);

    if (!guessNum || guessNum < minNum || guessNum > maxNum) {
      setFeedback('Please enter a valid number between 1 and 100.');
      return;
    }

    if (guessNum === answer) {
      setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
    } else {
      setAttempts((prev) => prev - 1);
      if (attempts === 1) {
        setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
      } else {
        setFeedback(guessNum > answer ? 'ðŸ“‰ Too high!' : 'ðŸ“ˆ Too low!');
      }
    }
    setGuess('');
  };

  const handleRestart = () => {
    setAnswer(Math.floor(Math.random() * (maxNum - minNum + 1)) + minNum);
    setAttempts(7);
    setFeedback('');
  };

  return (
    <div className="level-panel active">
      <h2>Level 1</h2>
      <input
        type="number"
        value={guess}
        onChange={(e) => setGuess(e.target.value)}
        placeholder="Enter your guess"
      />
      <button onClick={handleGuess}>Guess</button>
      <p>{feedback}</p>
      <p>Attempts Left: <span>{attempts}</span></p>
      <img src="./img/hinh-nen-desktop.jpg" alt="A beautiful background for Level 1" />
      <button onClick={handleRestart}>Restart Game</button>
    </div>
  );
};

export default Level1;
