import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../CSS/Level.css';

const LevelGame = ({ maxAttempts }) => {
  const [attempts, setAttempts] = useState(maxAttempts);
  const [feedback, setFeedback] = useState('');
  const [answer, setAnswer] = useState(Math.floor(Math.random() * 100) + 1);
  const navigate = useNavigate();

  const handleGuess = (guess) => {
    if (guess === answer) {
      setFeedback('ðŸŽ‰ Congratulations! You guessed the correct number!');
      setAttempts(maxAttempts);
    } else if (attempts <= 1) {
      setFeedback(`ðŸ˜ž Game Over! The number was ${answer}.`);
    } else {
      setFeedback(guess > answer ? 'ðŸ“‰ Too high!' : 'ðŸ“ˆ Too low!');
      setAttempts(attempts - 1);
    }
    document.getElementById('guess-input').value = '';
  };

  const handleRestart = () => {
    setAttempts(maxAttempts);
    setFeedback('');
    setAnswer(Math.floor(Math.random() * 100) + 1);
  };

  return (
    <div className="level-panel active">
      <h2>Level Game</h2>
      <input type="number" id="guess-input" placeholder="Enter your guess" />
      <button id="guess-button" onClick={() => handleGuess(Number(document.getElementById('guess-input').value))}>Guess</button>
      <p id="feedback">{feedback}</p>
      <p>Attempts Left: <span id="attempts">{attempts}</span></p>
      <button id="restart-button" className={feedback === '' ? 'hidden' : ''} onClick={handleRestart}> Restart Game</button>
      <button className="exit-btn" onClick={() => navigate('/choose')}>Exit</button>
    </div>
  );
};

export default LevelGame;
